import React from "react";
import ReactDOM from 'react-dom';


function Button(){
    return(
        <>
        {/* <a href="/">
            <button class="btn btn-outline-success" type="button">Login</button>
        </a> */}
        File Input: <input type="file" accept=".pdf" onChange={(event) => this.setState({ selectedFile: event.target.files[0] })} />
        <button className= 'btn btn-outline-success'type="submit" onChange={(event) => this.setState({ selectedFile: event.target.files[0] })}>submit</button></>

    )
}


document.addEventListener('DOMContentLoaded', () => {
    ReactDOM.render(
      <Button/>,
      document.getElementById('button')
    )
    })
