import React from "react";
import ReactDOM from 'react-dom'
// import MyComponent from "./count";

class MyComponent extends React.Component() {

  state ={
  shortCount:('')
}

  componentDidMount(){
    this.getCount()
  }



  getCount =() =>{
    fetch('http://127.0.0.1:3000/count')
    .then(response => response.json())
     .then (count => this.setData({ shortCount: count}))
  }


render() {
const num = this.state.shortCount
  return (
    <div className='count'>
     <p>value from react count {num}</p>
    </div>
  );
}
}

