import React from 'react';

function Body() {

  const textRef = useRef();

  const copyToClipboard = () => {
    let copyText = textRef.current.value;
    let isCopy = copy(copyText);

    if (isCopy) {
      toast.success("Copied to Clipboard");
    }
  };

  return (
    <div className='display-1'>
    <p>Number of URL's shortened</p>

    <a href="/home/view">
              <button class="btn btn-outline-success" type="button">List Of URL's Shortened</button>
              </a>
              <input value="Text to copy" disabled type="text" ref={textRef} />

<button onClick={copyToClipboard}>Copy</button>

<br />
<br />
<textarea placeholder="Paste here the copied text"></textarea>

    </div>

  );
}

export default Body;
