const url="Hi this is react body"
const [text, setText]= useState('')

const fetchInfo = () => {
  return fetch(url)
  .then ((res) => res.json())
  .then((d) => setText(d))
}

useEffect(() => {
  fetchInfo();
});

function handleCopy(){
  const copyText=navigator.clipboard.writeText(text)
  alert("Copied Successfully")
}

    return (
      <div className='container'>
          The Shortened Url is :<input type="text" name="text" value={text} />
          <button onClick={handleCopy}>Copy</button>
      </div>
  )
}

export default Copyurl
